package Basic;


	


	public class TypeCasting {

		public static void main(String[] args) {
			// TODO Auto-generated method stub
			long l = 54;
			int a = (int)54;
			System.out.println("a = "+a);
			String s = "45.56";
			double d = Double.valueOf(s);
			System.out.println("d ="+d);                    
			float f = 5.46f;
			long l1 = (long)f; 
			System.out.println("L1 ="+l1);
			
			
			

		}

	}


